package s.android.ffmpeg.util;import s.android.ffmpeg.http.AsyncHttpClient;import s.android.ffmpeg.http.AsyncHttpResponseHandler;import s.android.ffmpeg.http.RequestParams;public class RequestUtils {    private static AsyncHttpClient mClient = new AsyncHttpClient();    static {        mClient.setTimeout(15000);    }    /**     * @param url     * @param handler     * @函数名称 : post     * @功能描述 :不带参数     * @参数及返回值说明：     * @修改记录：     * @日期 ：2014-4-18 上午11:07:27 @修改人：邓风森     * @描述 ：     */    public static void post(String url, AsyncHttpResponseHandler handler)    {        post(url, null, handler);    }    /**     * @param url     * @param params     * @param handler     * @函数名称 : post     * @功能描述 :带参数     * @参数及返回值说明：     * @修改记录：     * @日期 ：2014-4-18 上午11:07:25 @修改人：邓风森     * @描述 ：     */    public static void post(String url, RequestParams params, AsyncHttpResponseHandler handler)    {        mClient.post(url, params, handler);    }    /**     * @param url     * @param handler     * @函数名称 : get     * @功能描述 :不带参数的get     * @参数及返回值说明：     * @修改记录：     * @日期 ：2014-4-18 上午11:07:43 @修改人：邓风森     * @描述 ：     */    public static void get(String url, AsyncHttpResponseHandler handler)    {        System.out.println("进入get");        mClient.get(url, null, handler);    }    /**     * @param url     * @param params     * @param handler     * @函数名称 : get     * @功能描述 :     * @参数及返回值说明：     * @修改记录：     * @日期 ：2014-4-18 上午11:07:58 @修改人：邓风森     * @描述 ：     */    public static void get(String url, RequestParams params, AsyncHttpResponseHandler handler)    {        System.out.println("进入get");        mClient.get(url, params, handler);    }}